function evaluate(params) {
  const { tableData, helpers, global_context_variables = {} } = params;

  /* â”€â”€ 0. Extract auxiliary counters (pass straight through) â”€â”€â”€â”€â”€ */
  const attemptNumber = Number(global_context_variables.attemptNumber ?? 1);
  const maxAttempts   = Number(global_context_variables.maxAttempts   ?? 2);
  const movesLeft     = Number(global_context_variables.movesLeft     ?? 1);

  /* â”€â”€ 1. Prep answer & mapping â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const paths   = tableData?.feedback?.paths ?? [];
  const mapping = JSON.parse(global_context_variables.numbers ?? "{}");
  const answer  = String(global_context_variables.ans ?? "")
                    .split("")
                    .map(Number);        /* "95" â†’ [9,5] */

  /* â”€â”€ 2. Allocate result matrices â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const rows = tableData.cells.length;
  const cols = Math.max(...tableData.cells.map(r => r.length));

  const cell_level_is_correct      = Array.from({ length: rows }, () => Array(cols).fill(false));
  const cell_path_level_is_correct = Array.from({ length: rows }, () => Array(cols).fill({}));

  /* â”€â”€ 3. Helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const arraysEqual = (a, b) => a.length === b.length && a.every((v, i) => v === b[i]);

  const digitForPattern = (pattern) => {
    for (const key in mapping) {
      for (const sub of mapping[key]) {
        if (arraysEqual(sub, pattern)) return Number(key);
      }
    }
    return undefined;                             // no match
  };

  /* â”€â”€ 4. Evaluate each path â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const pathDigits     = [];      // like [9, 5]
  const pathIsCorrect  = [];
  let   isNumberWrong  = false;

  paths.forEach((path, idx) => {
    /* Build 0/1 pattern for this path */
    const pattern = path.cells.map(([r, c]) => {
      const cell = tableData.cells[r][c];
      if (!cell.dragDrop?.enabled) return 1;          // fixed stick
      const { cellInputValue } = helpers.getCellInputValue(cell);
      return cellInputValue === "1" ? 1 : 0;
    });

    const digit = digitForPattern(pattern);
    pathDigits.push(digit);

    if (digit === undefined) {
      isNumberWrong = true;
      pathIsCorrect[idx] = false;
    } else {
      const expected = answer[idx];   // could be undefined if answer shorter
      pathIsCorrect[idx] = digit === expected;
    }
  });

  /* Extra digits in target answer that have no path â†’ incorrect */
  for (let i = paths.length; i < answer.length; i++) pathIsCorrect[i] = false;

  const is_correct = pathIsCorrect.every(Boolean);

  /* â”€â”€ 5. Populate per-cell correctness matrices â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  paths.forEach((path, pathIdx) => {
    const correct = pathIsCorrect[pathIdx] ?? false;
    const key     = String(pathIdx);

    path.cells.forEach(([r, c]) => {
      cell_level_is_correct[r][c] = is_correct;
      cell_path_level_is_correct[r][c] = {
        ...cell_path_level_is_correct[r][c],
        [key]: correct,
      };
    });
  });

  /* â”€â”€ 6. Build the integer the learner formed, if any â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const madeNum = pathDigits.every(d => typeof d === "number")
                  ? Number(pathDigits.join(""))
                  : null;     // invalid / incomplete number

  /* â”€â”€ 7. Ship EVERYTHING needed by feedback layer â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  return {
    /* verdicts  */
    is_correct,
    isNumberWrong,
    madeNum,

    /* attempt / move bookkeeping  */
    attemptNumber,
    maxAttempts,
    movesLeft,

    /* still return matrices if UI needs them */
    cell_level_is_correct,
    cell_path_level_is_correct,
  };
}

