  function levenshteinDistance(s1, s2) {
    if (s1.length > s2.length) {
      [s1, s2] = [s2, s1];
    }

    let distances = Array.from({ length: s1.length + 1 }, (_, i) => i);
    for (let i = 0; i < s2.length; i++) {
      let newDistances = [i + 1];
      for (let j = 0; j < s1.length; j++) {
        if (s1[j] === s2[i]) {
          newDistances.push(distances[j]);
        } else {
          newDistances.push(1 + Math.min(distances[j], distances[j + 1], newDistances[newDistances.length - 1]));
        }
      }
      distances = newDistances;
    }

    return distances[s1.length];
  }

